    private void limparCampos() {
        txtNome.setText("");
        txtPreco.setText("");
        txtQuantidade.setText("");
    }

    private boolean emptyFields() {

        boolean empty = true;

        if (txtNome.getText().trim().isEmpty()) {

            JOptionPane.showMessageDialog(rootPane, "ATEN√á√ÉO! Nome n√£o pode ser vazio.");
        } else if (txtPreco.getText().trim().isEmpty()) {

            JOptionPane.showMessageDialog(rootPane, "ATEN√á√ÉO! Pre√ßo n√£o pode ser vazio.");
        } else if (txtQuantidade.getText().trim().isEmpty()) {

            JOptionPane.showMessageDialog(rootPane, "ATEN√á√ÉO! Quantidade n√£o pode ser vazio.");
        } else {

            empty = false;
        }

        return empty;
    }

